import { useEffect, useRef, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";

declare global {
  interface Window {
    L: any;
  }
}

interface Zone {
  name: string;
  lat: number;
  lng: number;
  density: 'low' | 'medium' | 'high' | 'critical';
  count: number;
}

export default function LiveCrowdMap() {
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<any>(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);

  const { data: stats } = useQuery<{totalAttendees?: number}>({
    queryKey: ['/api/stats'],
    refetchInterval: 10000, // Refresh every 10 seconds
  });

  // Mock zones data - in production this would come from real-time analysis
  const zones: Zone[] = [
    { name: 'Ram Ghat', lat: 23.1765, lng: 75.7885, density: 'critical', count: 2847 },
    { name: 'Mahakal Temple', lat: 23.1825, lng: 75.7685, density: 'high', count: 1523 },
    { name: 'Triveni Sangam', lat: 23.1705, lng: 75.7785, density: 'medium', count: 892 },
    { name: 'Transit Hub-A', lat: 23.1695, lng: 75.7685, density: 'low', count: 234 }
  ];

  useEffect(() => {
    // Load Leaflet script if not already loaded
    if (!window.L) {
      const script = document.createElement('script');
      script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
      script.onload = () => {
        setIsMapLoaded(true);
      };
      document.head.appendChild(script);
    } else {
      setIsMapLoaded(true);
    }
  }, []);

  useEffect(() => {
    if (!isMapLoaded || !mapRef.current || mapInstanceRef.current) return;

    // Initialize Leaflet Map
    const map = window.L.map(mapRef.current).setView([23.1765, 75.7885], 15);

    window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Â© OpenStreetMap contributors'
    }).addTo(map);

    // Add zone circles
    zones.forEach(zone => {
      const color = zone.density === 'critical' ? '#DC143C' : 
                   zone.density === 'high' ? '#FF6B35' :
                   zone.density === 'medium' ? '#FFD700' : '#228B22';

      window.L.circle([zone.lat, zone.lng], {
        color: color,
        fillColor: color,
        fillOpacity: 0.3,
        radius: zone.count / 2
      }).addTo(map)
      .bindPopup(`<strong>${zone.name}</strong><br>Density: ${zone.density}<br>Count: ${zone.count}`);
    });

    mapInstanceRef.current = map;

    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
    };
  }, [isMapLoaded]);

  return (
    <Card className="spiritual-border shadow-lg">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-card-foreground flex items-center space-x-3">
            <i className="fas fa-map text-primary"></i>
            <span>Live Crowd Density â€¢ à¤­à¥€à¤¡à¤¼ à¤˜à¤¨à¤¤à¥à¤µ</span>
          </h2>
          <div className="flex items-center space-x-4">
            <div className="text-sm text-muted-foreground">
              Total Pilgrims: <span className="font-bold text-primary" data-testid="text-total-pilgrims">
                {stats?.totalAttendees?.toLocaleString() || '1,247,832'}
              </span>
            </div>
            <Button 
              variant="outline" 
              size="sm"
              data-testid="button-fullscreen-map"
            >
              <i className="fas fa-expand-arrows-alt mr-2"></i>Fullscreen
            </Button>
          </div>
        </div>

        {/* Map Container */}
        <div className="relative">
          <div 
            ref={mapRef}
            className="h-96 rounded-lg border-2 border-border overflow-hidden"
            data-testid="map-container"
          >
            {!isMapLoaded && (
              <div className="h-full bg-muted flex items-center justify-center">
                <div className="text-center">
                  <i className="fas fa-map-marked-alt text-4xl text-muted-foreground mb-2"></i>
                  <p className="text-muted-foreground">Interactive Map Loading...</p>
                </div>
              </div>
            )}
          </div>

          {/* Heatmap Legend */}
          <div className="absolute bottom-4 left-4 bg-card/90 backdrop-blur rounded-lg p-3 border border-border">
            <h4 className="text-sm font-semibold mb-2 text-card-foreground">Crowd Density</h4>
            <div className="space-y-1">
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-success rounded"></div>
                <span className="text-xs">Low (&lt; 0.2)</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-accent rounded"></div>
                <span className="text-xs">Medium (0.2-0.5)</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-primary rounded"></div>
                <span className="text-xs">High (0.5-0.8)</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-destructive rounded"></div>
                <span className="text-xs">Critical (&gt; 0.8)</span>
              </div>
            </div>
          </div>

          {/* Zone Stats Overlay */}
          <div className="absolute top-4 right-4 bg-card/90 backdrop-blur rounded-lg p-3 border border-border">
            <h4 className="text-sm font-semibold mb-2 text-card-foreground">Active Zones</h4>
            <div className="space-y-1 text-xs">
              {zones.map((zone, index) => (
                <div key={index} className="flex justify-between">
                  <span>{zone.name}:</span>
                  <span className={`font-bold ${
                    zone.density === 'critical' ? 'text-destructive' :
                    zone.density === 'high' ? 'text-primary' :
                    zone.density === 'medium' ? 'text-accent' : 'text-success'
                  }`}>
                    {zone.density.charAt(0).toUpperCase() + zone.density.slice(1)}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/LostAndFound.tsx.content:
import { useState, useRef } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Link } from "wouter";

export default function LostAndFound() {
  const { toast } = useToast();
  const [step, setStep] = useState<1 | 2>(1);
  const [searchMediaFile, setSearchMediaFile] = useState<File | null>(null);
  const [targetPersonFile, setTargetPersonFile] = useState<File | null>(null);
  const [searchResult, setSearchResult] = useState<any>(null);
  const searchMediaRef = useRef<HTMLInputElement>(null);
  const targetPersonRef = useRef<HTMLInputElement>(null);

  // Two-step search mutation
  const twoStepSearchMutation = useMutation({
    mutationFn: async ({ searchMedia, targetPerson }: { searchMedia: File; targetPerson: File }) => {
      const formData = new FormData();
      formData.append('searchMedia', searchMedia);
      formData.append('targetPerson', targetPerson);

      try {
        const response = await fetch('/api/lost-persons/two-step-search', {
          method: 'POST',
          body: formData,
          credentials: 'include'
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(errorData.message || errorData.error || `HTTP ${response.status}: ${response.statusText}`);
        }

        return response.json();
      } catch (error) {
        console.error('Fetch error:', error);
        if (error instanceof TypeError && error.message.includes('fetch')) {
          throw new Error('Network connection failed. Please check your internet connection.');
        }
        throw error;
      }
    },
    onSuccess: (data) => {
      setSearchResult(data);
      toast({
        title: "Search Complete",
        description: data.searchResult.found 
          ? `Person found with ${data.searchResult.confidence}% confidence!` 
          : "Person not found in the uploaded media.",
      });
    },
    onError: (error: any) => {
      console.error('Search error:', error);
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      // Extract detailed error message
      let errorMessage = "Unable to perform search. Please try again.";
      if (error?.message) {
        errorMessage = error.message;
      } else if (typeof error === 'string') {
        errorMessage = error;
      }

      toast({
        title: "Search Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleSearchMediaSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setSearchMediaFile(file);
      setSearchResult(null);
    }
  };

  const handleTargetPersonSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setTargetPersonFile(file);
    }
  };

  const handleStepComplete = () => {
    if (step === 1 && searchMediaFile) {
      setStep(2);
    } else if (step === 2 && searchMediaFile && targetPersonFile) {
      twoStepSearchMutation.mutate({ searchMedia: searchMediaFile, targetPerson: targetPersonFile });
    }
  };

  const resetSearch = () => {
    setStep(1);
    setSearchMediaFile(null);
    setTargetPersonFile(null);
    setSearchResult(null);
    if (searchMediaRef.current) searchMediaRef.current.value = '';
    if (targetPersonRef.current) targetPersonRef.current.value = '';
  };

  return (
    <Card className="spiritual-border shadow-lg">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-bold text-card-foreground flex items-center space-x-2">
            <i className="fas fa-search text-primary"></i>
            <span>Lost & Found AI Search</span>
          </h3>
          <div className="flex items-center space-x-2 text-sm text-muted-foreground">
            <div className={`w-6 h-6 rounded-full ${step >= 1 ? 'bg-primary text-primary-foreground' : 'bg-muted'} flex items-center justify-center text-xs font-bold`}>1</div>
            <div className="w-8 h-0.5 bg-muted"></div>
            <div className={`w-6 h-6 rounded-full ${step >= 2 ? 'bg-primary text-primary-foreground' : 'bg-muted'} flex items-center justify-center text-xs font-bold`}>2</div>
          </div>
        </div>

        <div className="space-y-4">
          {/* Step 1: Upload Search Media */}
          {step === 1 && (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-card-foreground mb-2">
                  Step 1: Upload Search Media (Image/Video) ðŸ“¸
                </label>
                <p className="text-xs text-muted-foreground mb-3">
                  Upload an image or video that might contain the missing person
                </p>
                <div className="border-2 border-dashed border-border rounded-lg p-4 text-center">
                  <i className="fas fa-cloud-upload-alt text-muted-foreground text-2xl mb-2"></i>
                  <p className="text-sm text-muted-foreground mb-2">Drop image/video or click to upload</p>
                  <Input
                    ref={searchMediaRef}
                    type="file"
                    accept="image/*,video/*"
                    onChange={handleSearchMediaSelect}
                    className="max-w-full"
                    data-testid="input-search-media"
                  />
                </div>
                {searchMediaFile && (
                  <div className="mt-2 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg">
                    <p className="text-sm text-green-700 dark:text-green-300">
                      âœ… Selected: {searchMediaFile.name} ({searchMediaFile.type.startsWith('video/') ? 'Video' : 'Image'})
                    </p>
                  </div>
                )}
              </div>

              <Button 
                className="w-full"
                onClick={handleStepComplete}
                disabled={!searchMediaFile}
                data-testid="button-next-step"
              >
                <i className="fas fa-arrow-right mr-2"></i>
                Next: Upload Target Person
              </Button>
            </div>
          )}

          {/* Step 2: Upload Target Person */}
          {step === 2 && (
            <div className="space-y-4">
              <div className="p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                <p className="text-sm text-blue-700 dark:text-blue-300">
                  âœ… Search media uploaded: {searchMediaFile?.name}
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-card-foreground mb-2">
                  Step 2: Upload Target Person Image ðŸ‘¤
                </label>
                <p className="text-xs text-muted-foreground mb-3">
                  Upload a clear photo of the person you're looking for
                </p>
                <div className="border-2 border-dashed border-border rounded-lg p-4 text-center">
                  <i className="fas fa-user text-muted-foreground text-2xl mb-2"></i>
                  <p className="text-sm text-muted-foreground mb-2">Drop target person image</p>
                  <Input
                    ref={targetPersonRef}
                    type="file"
                    accept="image/*"
                    onChange={handleTargetPersonSelect}
                    className="max-w-full"
                    data-testid="input-target-person"
                  />
                </div>
                {targetPersonFile && (
                  <div className="mt-2 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg">
                    <p className="text-sm text-green-700 dark:text-green-300">
                      âœ… Selected: {targetPersonFile.name}
                    </p>
                  </div>
                )}
              </div>

              <div className="flex space-x-2">
                <Button 
                  variant="outline"
                  onClick={() => setStep(1)}
                  className="flex-1"
                  data-testid="button-back-step"
                >
                  <i className="fas fa-arrow-left mr-2"></i>
                  Back
                </Button>
                <Button 
                  className="flex-1"
                  onClick={handleStepComplete}
                  disabled={!targetPersonFile || twoStepSearchMutation.isPending}
                  data-testid="button-start-search"
                >
                  <i className="fas fa-search mr-2"></i>
                  {twoStepSearchMutation.isPending ? 'AI Analyzing...' : 'Start Enhanced AI Search'}
                </Button>
              </div>
            </div>
          )}

          {/* Search Results */}
          {searchResult && (
            <div className="mt-6 space-y-3">
              <div className="border-t pt-4">
                <h4 className="text-sm font-semibold text-card-foreground mb-3 flex items-center">
                  <i className="fas fa-robot mr-2 text-primary"></i>
                  AI Search Results
                </h4>

                {searchResult.searchResult.found ? (
                  <div className="p-4 bg-green-50 dark:bg-green-900/20 rounded-lg space-y-3">
                    <div className="flex items-center space-x-2">
                      <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                        <i className="fas fa-check text-white text-sm"></i>
                      </div>
                      <div>
                        <p className="font-semibold text-green-700 dark:text-green-300">
                          Person Found! ({searchResult.searchResult.confidence}% confidence)
                        </p>
                        <p className="text-sm text-green-600 dark:text-green-400">
                          ðŸ“ {searchResult.searchResult.location}
                        </p>
                      </div>
                    </div>

                    <div className="bg-white dark:bg-gray-800 p-3 rounded border">
                      <p className="text-sm text-gray-700 dark:text-gray-300">
                        ðŸ¤– <strong>AI Analysis:</strong> {searchResult.searchResult.description}
                      </p>
                    </div>

                    {searchResult.searchResult.matchDetails && (
                      <div className="bg-white dark:bg-gray-800 p-3 rounded border space-y-2">
                        <p className="text-xs font-semibold text-gray-600 dark:text-gray-400 mb-1">
                          ðŸ” Enhanced AI Analysis ({searchResult.searchResult.matchDetails.analysisSteps || 1} steps)
                        </p>

                        {searchResult.searchResult.matchDetails.targetFeatures && (
                          <div className="text-xs">
                            <p className="font-medium text-blue-600 dark:text-blue-400">Target Person Features:</p>
                            <p className="text-gray-600 dark:text-gray-400 ml-2">
                              {JSON.stringify(searchResult.searchResult.matchDetails.targetFeatures).slice(0, 200)}...
                            </p>
                          </div>
                        )}

                        {searchResult.searchResult.matchDetails.detectedPeople && (
                          <div className="text-xs">
                            <p className="font-medium text-green-600 dark:text-green-400">
                              People Detected: {searchResult.searchResult.matchDetails.detectedPeople.people?.length || 'Unknown'}
                            </p>
                            <p className="text-gray-600 dark:text-gray-400 ml-2">
                              {JSON.stringify(searchResult.searchResult.matchDetails.detectedPeople).slice(0, 200)}...
                            </p>
                          </div>
                        )}

                        {searchResult.searchResult.matchDetails.comparison && (
                          <div className="text-xs">
                            <p className="font-medium text-purple-600 dark:text-purple-400">Final Comparison:</p>
                            <p className="text-gray-600 dark:text-gray-400 ml-2">
                              {JSON.stringify(searchResult.searchResult.matchDetails.comparison).slice(0, 200)}...
                            </p>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="p-4 bg-gray-50 dark:bg-gray-900/20 rounded-lg">
                    <div className="flex items-center space-x-2 mb-2">
                      <div className="w-8 h-8 bg-gray-400 rounded-full flex items-center justify-center">
                        <i className="fas fa-times text-white text-sm"></i>
                      </div>
                      <p className="font-semibold text-gray-700 dark:text-gray-300">
                        Person Not Found
                      </p>
                    </div>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      ðŸ¤– AI Analysis: {searchResult.searchResult.description || 'The target person was not detected in the uploaded media.'}
                    </p>
                  </div>
                )}

                <Button 
                  variant="outline"
                  onClick={resetSearch}
                  className="w-full mt-3"
                  data-testid="button-new-search"
                >
                  <i className="fas fa-redo mr-2"></i>
                  Start New Search
                </Button>
              </div>
            </div>
          )}

          <div className="mt-6 border-t pt-4">
            <Link href="/lost-and-found">
              <Button className="w-full bg-primary text-primary-foreground hover:bg-primary/90" data-testid="button-view-all-cases">
                <i className="fas fa-eye mr-2"></i>
                View All Lost Person Cases
              </Button>
            </Link>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

