            <i className="fas fa-shield-alt text-2xl mb-2"></i>
            <div className="text-sm font-bold">Lockdown</div>
            <div className="text-xs opacity-80">Emergency protocol</div>
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

