      lastUpdate: new Date(Date.now() - 45000).toISOString(),
      thumbnail: '/attached_assets/generated_images/Sacred_river_monitoring_background_dcf11067.png'
    },
    {
      id: 'cam003',
      name: 'Triveni Sangam Point',
      zone: 'Triveni Sangam',
      status: 'online', 
      crowdDensity: 'medium',
      peopleCount: 892,
      riskLevel: 'low',
      lastUpdate: new Date(Date.now() - 15000).toISOString(),
      thumbnail: '/attached_assets/generated_images/Mahakumbh_devotee_families_scene_12a2eed8.png'
    },
    {
      id: 'cam004',
      name: 'Transit Hub-A Security',
      zone: 'Transit Hub-A',
      status: 'online',
      crowdDensity: 'low',
      peopleCount: 234,
      riskLevel: 'none',
      lastUpdate: new Date(Date.now() - 60000).toISOString(),
      thumbnail: '/attached_assets/generated_images/Mahakumbh_festival_spiritual_logo_8630bcb7.png'
    },
    {
      id: 'cam005',
      name: 'Medical Zone-C Monitor',
      zone: 'Medical Zone-C',
      status: 'maintenance',
      crowdDensity: 'low',
      peopleCount: 0,
      riskLevel: 'none',
      lastUpdate: new Date(Date.now() - 300000).toISOString(),
      thumbnail: ''
    },
    {
      id: 'cam006',
      name: 'Parking Area-B Overflow',
      zone: 'Parking Zone-B',
      status: 'online',
      crowdDensity: 'high',
      peopleCount: 1847,
      riskLevel: 'medium',
      lastUpdate: new Date(Date.now() - 20000).toISOString(),
      thumbnail: '/attached_assets/generated_images/Sacred_emergency_alert_mandala_28ae0dbf.png'
    },
    // Demo feed placeholders when in demo mode
    ...(demoMode ? [
      {
        id: 'demo001',
        name: 'DEMO - Uploaded Video Feed',
        zone: 'Demo Zone',
        status: 'online' as const,
        crowdDensity: 'high' as const,
        peopleCount: 1847,
        riskLevel: 'medium' as const,
        lastUpdate: new Date().toISOString(),
        thumbnail: '/attached_assets/generated_images/Dense_Mahakumbh_crowd_scene_25e9b4b2.png'
      }
    ] : [])
  ];

  const handleVideoUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;

    const file = files[0];

    if (!file.type.startsWith('video/')) {
      toast({
        title: "Invalid File",
        description: "Please upload a video file for demonstration",
        variant: "destructive"
      });
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);

    try {
      const progressInterval = setInterval(() => {
        setUploadProgress(prev => {
          if (prev >= 100) {
            clearInterval(progressInterval);
            return 100;
          }
          return prev + Math.random() * 15;
        });
      }, 200);

      const formData = new FormData();
      formData.append('video', file);
      formData.append('demo_type', 'crowd_analysis');

      const response = await fetch('/api/prototype/video', {
        method: 'POST',
        body: formData,
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setUploadProgress(100);

      toast({
        title: "Demo Video Uploaded",
        description: `${file.name} is now available as a simulated camera feed`,
      });

    } catch (error) {
      toast({
        title: "Upload Failed", 
        description: "Failed to upload demonstration video",
        variant: "destructive"
      });
    } finally {
      setIsUploading(false);
      setTimeout(() => setUploadProgress(0), 1000);
    }
  };

  const handleAnalyzeFeed = async (feedId: string) => {
    setIsAnalyzing(true);
    try {
      // Simulate AI analysis upload
      const formData = new FormData();
      formData.append('source_id', feedId);

      // Create a mock file for demonstration
      const mockImageBlob = new Blob(['mock camera frame'], { type: 'image/jpeg' });
      formData.append('file', mockImageBlob, 'camera_frame.jpg');

      const response = await fetch('/api/frames/upload', {
        method: 'POST',
        body: formData,
        credentials: 'include'
      });

      if (response.ok) {
        // Analysis will trigger events if risks detected
        console.log('Frame analysis initiated for', feedId);
      }
    } catch (error) {
      console.error('Analysis failed:', error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getDensityColor = (density: string) => {
    switch (density) {
      case 'critical': return 'bg-red-600';
      case 'high': return 'bg-orange-500';
      case 'medium': return 'bg-yellow-500';
      case 'low': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  const getRiskBorder = (risk: string) => {
    switch (risk) {
      case 'critical': return 'border-red-500 border-4';
      case 'high': return 'border-orange-500 border-3';
      case 'medium': return 'border-yellow-500 border-2';
      case 'low': return 'border-green-500 border-1';
      default: return 'border-gray-300 border-1';
    }
  };

  return (
    <Card className="spiritual-border shadow-lg sacred-card divine-glow">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-card-foreground flex items-center space-x-3 font-vintage text-shadow-golden floating-om">
            <i className="fas fa-video text-primary divine-glow"></i>
            <span>Divine Vision Feeds â€¢ à¤¦à¤¿à¤µà¥à¤¯ à¤¦à¥ƒà¤¶à¥à¤¯</span>
          </h2>
          <div className="flex items-center space-x-3">
            <Badge 
              variant={demoMode ? "default" : "outline"} 
              className="cursor-pointer" 
              onClick={() => setDemoMode(!demoMode)}
              data-testid="button-toggle-demo-mode"
            >
              {demoMode ? "ðŸŽ¬ Demo Mode" : "ðŸ“¹ Live Mode"}
            </Badge>
            {demoMode && (
              <div className="flex items-center space-x-2">
                <Input
                  type="file"
                  accept="video/*"
                  ref={fileInputRef}
                  onChange={handleVideoUpload}
                  className="hidden"
                  data-testid="input-demo-video-upload"
                />
                <Button
                  size="sm"
                  onClick={() => fileInputRef.current?.click()}
                  disabled={isUploading}
                  data-testid="button-upload-demo-video"
                >
                  <i className="fas fa-upload mr-1"></i>
                  {isUploading ? 'Uploading...' : 'Add Demo Video'}
                </Button>
              </div>
            )}
          </div>
        </div>

        {/* Upload Progress */}
        {isUploading && uploadProgress > 0 && (
          <div className="mb-4 p-3 bg-muted rounded-lg">
            <div className="flex justify-between text-sm mb-2">
              <span>Uploading demonstration video...</span>
              <span>{Math.round(uploadProgress)}%</span>
            </div>
            <div className="w-full bg-background rounded-full h-2">
              <div 
                className="bg-primary h-2 rounded-full transition-all duration-300"
                style={{ width: `${uploadProgress}%` }}
              ></div>
            </div>
          </div>
        )}

        <div className="grid grid-cols-2 lg:grid-cols-3 gap-4">
          {mockFeeds.map((feed) => (
            <div
              key={feed.id}
              className={`relative rounded-lg overflow-hidden cursor-pointer transition-all duration-300 ${getRiskBorder(feed.riskLevel)} ${selectedFeed === feed.id ? 'ring-4 ring-primary' : ''}`}
              onClick={() => setSelectedFeed(selectedFeed === feed.id ? null : feed.id)}
              data-testid={`camera-feed-${feed.id}`}
            >
              {/* Camera Feed Display */}
              <div className="aspect-video bg-black relative">
                {feed.status === 'maintenance' ? (
                  <div className="absolute inset-0 flex items-center justify-center bg-gray-800">
                    <div className="text-center text-gray-400">
                      <i className="fas fa-tools text-2xl mb-2"></i>
                      <p className="text-sm">Under Maintenance</p>
                    </div>
                  </div>
                ) : feed.thumbnail ? (
                  <img
                    src={feed.thumbnail}
                    alt={feed.name}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="absolute inset-0 flex items-center justify-center bg-gray-800">
                    <div className="text-center text-gray-400">
                      <i className="fas fa-camera text-2xl mb-2"></i>
                      <p className="text-sm">Camera Feed</p>
                    </div>
                  </div>
                )}

                {/* Status Indicator */}
                <div className={`absolute top-2 right-2 w-3 h-3 rounded-full ${feed.status === 'online' ? 'bg-green-500' : feed.status === 'maintenance' ? 'bg-yellow-500' : 'bg-red-500'} ${feed.status === 'online' ? 'pulse-ring' : ''}`}></div>

                {/* Crowd Density Badge */}
                {feed.status === 'online' && (
                  <div className={`absolute top-2 left-2 px-2 py-1 text-xs font-bold text-white rounded ${getDensityColor(feed.crowdDensity)}`}>
                    {feed.crowdDensity.toUpperCase()}
                  </div>
                )}

                {/* People Count */}
                {feed.status === 'online' && (
                  <div className="absolute bottom-2 left-2 bg-black bg-opacity-75 text-white px-2 py-1 text-xs rounded">
                    ðŸ‘¥ {feed.peopleCount.toLocaleString()}
                  </div>
                )}

                {/* Live Indicator */}
                {feed.status === 'online' && (
                  <div className="absolute bottom-2 right-2 bg-red-600 text-white px-2 py-1 text-xs rounded flex items-center space-x-1">
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    <span>LIVE</span>
                  </div>
                )}
              </div>

              {/* Camera Info */}
              <div className="p-3 bg-card">
                <h4 className="font-semibold text-card-foreground text-sm truncate">{feed.name}</h4>
                <p className="text-xs text-muted-foreground truncate">{feed.zone}</p>
                <div className="flex items-center justify-between mt-2">
                  <span className="text-xs text-muted-foreground">
                    {new Date(feed.lastUpdate).toLocaleTimeString('en-IN')}
                  </span>
                  {feed.status === 'online' && (
                    <Button
                      size="sm"
                      variant="outline"
                      className="text-xs h-6"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleAnalyzeFeed(feed.id);
                      }}
                      disabled={isAnalyzing}
                      data-testid={`analyze-button-${feed.id}`}
                    >
                      {isAnalyzing ? (
                        <i className="fas fa-spinner fa-spin"></i>
                      ) : (
                        <i className="fas fa-brain"></i>
                      )}
                    </Button>
                  )}
                </div>
              </div>

              {/* Expanded Info Panel */}
              {selectedFeed === feed.id && feed.status === 'online' && (
                <div className="absolute inset-0 bg-black bg-opacity-90 text-white p-4 flex flex-col justify-center">
                  <div className="text-center">
                    <h3 className="font-bold mb-2">{feed.name}</h3>
                    <div className="space-y-1 text-sm">
                      <p>Zone: {feed.zone}</p>
                      <p>Density: <span className={`font-bold ${feed.crowdDensity === 'critical' ? 'text-red-400' : feed.crowdDensity === 'high' ? 'text-orange-400' : feed.crowdDensity === 'medium' ? 'text-yellow-400' : 'text-green-400'}`}>{feed.crowdDensity.toUpperCase()}</span></p>
                      <p>People Count: {feed.peopleCount.toLocaleString()}</p>
                      <p>Risk Level: <span className={`font-bold ${feed.riskLevel === 'critical' ? 'text-red-400' : feed.riskLevel === 'high' ? 'text-orange-400' : feed.riskLevel === 'medium' ? 'text-yellow-400' : 'text-green-400'}`}>{feed.riskLevel.toUpperCase()}</span></p>
                    </div>
                    <Button
                      size="sm"
                      className="mt-3 bg-primary text-primary-foreground"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleAnalyzeFeed(feed.id);
                      }}
                      disabled={isAnalyzing}
                    >
                      {isAnalyzing ? (
                        <>
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                          Analyzing...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-brain mr-2"></i>
                          AI Analysis
                        </>
                      )}
                    </Button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Control Panel */}
        <div className="mt-6 p-4 bg-muted rounded-lg border border-border">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4 text-sm text-muted-foreground">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-green-500 rounded-full pulse-ring"></div>
                <span>{mockFeeds.filter(f => f.status === 'online').length} Active</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
                <span>{mockFeeds.filter(f => f.status === 'maintenance').length} Maintenance</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-red-500 rounded-full"></div>
                <span>Total: {mockFeeds.reduce((sum, f) => sum + f.peopleCount, 0).toLocaleString()} Devotees</span>
              </div>
            </div>
            <Button 
              size="sm" 
              variant="outline"
              onClick={() => window.location.reload()}
              data-testid="refresh-feeds-button"
            >
              <i className="fas fa-refresh mr-2"></i>
              Refresh All
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/QuickActionsEnhanc.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";

export default function QuickActionsEnhanced() {
  const { toast } = useToast();
  const [isDeploying, setIsDeploying] = useState(false);
  const [isLockdown, setIsLockdown] = useState(false);
  const [isRouting, setIsRouting] = useState(false);

  const handleEmergencyAlert = async () => {
    if (confirm('ðŸš¨ Broadcast emergency alert to all zones? This will notify all pilgrims and volunteers.')) {
      try {
        const response = await fetch('/api/alerts/generate', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          credentials: 'include',
          body: JSON.stringify({
            zone: 'all',
            languages: ['hindi', 'english'],
            alertType: 'emergency'
          })
        });

        if (response.ok) {
          toast({
            title: "ðŸ”Š Emergency Alert Broadcast",
            description: "Multilingual emergency alert sent to all zones, loudspeakers, and mobile devices.",
            variant: "destructive",
          });
        }
      } catch (error) {
        toast({
          title: "Alert Failed",
          description: "Unable to broadcast emergency alert.",
          variant: "destructive",
        });
      }
    }
  };

  const handleDeployTeams = async () => {
    setIsDeploying(true);
    try {
      // Deploy multiple volunteers to high-risk zones
      const deployments = ['v001', 'v007', 'v012'];
      let successCount = 0;

      for (const volunteerId of deployments) {
        try {
          const response = await fetch(`/api/volunteers/${volunteerId}/deploy`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            credentials: 'include',
            body: JSON.stringify({
              zone: 'Ram Ghat Zone-A',
              priority: 'high'
            })
          });
          if (response.ok) successCount++;
        } catch (err) {
          console.error(`Failed to deploy ${volunteerId}:`, err);
        }
      }

      toast({
        title: "ðŸš Response Teams Deployed",
        description: `${successCount} emergency response teams dispatched to critical zones with real-time tracking.`,
      });
    } catch (error) {
      toast({
        title: "Deployment Failed",
        description: "Unable to deploy response teams.",
        variant: "destructive",
      });
    } finally {
      setIsDeploying(false);
    }
  };

  const handleCrowdRouting = async () => {
    setIsRouting(true);
    try {
      const response = await fetch('/api/alerts/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          zone: 'all',
          languages: ['hindi', 'english', 'marathi'],
          alertType: 'crowd_guidance'
        })
      });

      if (response.ok) {
        toast({
          title: "ðŸ§­ Crowd Routing Activated",
          description: "Directing pilgrims to alternate routes. AI-powered flow management protocols active.",
        });
      }
    } catch (error) {
      toast({
        title: "Routing Failed",
        description: "Unable to activate crowd routing.",
        variant: "destructive",
      });
    } finally {
      setIsRouting(false);
    }
  };

  const handleLockdown = async () => {
    const newLockdownState = !isLockdown;
    if (confirm(`${newLockdownState ? 'ðŸ”’ Activate' : 'ðŸ”“ Deactivate'} emergency lockdown protocol? This will ${newLockdownState ? 'restrict' : 'restore'} access to critical areas.`)) {
      try {
        const response = await fetch('/api/alerts/generate', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          credentials: 'include',
          body: JSON.stringify({
            zone: 'all',
            languages: ['hindi', 'english'],
            alertType: newLockdownState ? 'lockdown' : 'all_clear'
          })
        });

        if (response.ok) {
          setIsLockdown(newLockdownState);
          toast({
            title: newLockdownState ? "ðŸ”’ Emergency Lockdown Active" : "ðŸŸ¢ Lockdown Lifted",
            description: newLockdownState ? 
              "All zones secured. Emergency protocol active. Only authorized personnel allowed." : 
              "Normal operations resumed. All areas accessible to pilgrims.",
            variant: newLockdownState ? "destructive" : "default",
          });
        }
      } catch (error) {
        toast({
          title: "Lockdown Failed",
          description: "Unable to toggle lockdown status.",
          variant: "destructive",
        });
      }
    }
  };

  return (
    <Card className="spiritual-border shadow-lg sacred-card sacred-glow">
      <CardContent className="p-6">
        <h2 className="text-xl font-bold text-card-foreground mb-6 flex items-center space-x-3 font-vintage text-shadow-golden">
          <i className="fas fa-bolt text-primary sacred-glow"></i>
          <span>Divine Actions â€¢ à¤¦à¤¿à¤µà¥à¤¯ à¤•à¤¾à¤°à¥à¤¯</span>
        </h2>

        <div className="grid grid-cols-2 gap-4">
          <Button 
            className="bg-destructive text-destructive-foreground p-4 h-auto flex-col hover:bg-destructive/90 sacred-glow transition-all duration-300"
            onClick={handleEmergencyAlert}
            data-testid="button-emergency-alert"
          >
            <i className="fas fa-bullhorn text-2xl mb-2 animate-pulse"></i>
            <div className="text-sm font-bold">Emergency Alert</div>
            <div className="text-xs opacity-80">Divine Protection Broadcast</div>
          </Button>

          <Button 
            className="bg-primary text-primary-foreground p-4 h-auto flex-col hover:bg-primary/90 sacred-glow transition-all duration-300"
            onClick={handleDeployTeams}
            disabled={isDeploying}
            data-testid="button-deploy-teams"
          >
            <i className={`fas fa-users-cog text-2xl mb-2 ${isDeploying ? 'animate-spin' : ''}`}></i>
            <div className="text-sm font-bold">{isDeploying ? 'Deploying...' : 'Deploy Teams'}</div>
            <div className="text-xs opacity-80">Sacred Guardians</div>
          </Button>

          <Button 
            className="bg-accent text-accent-foreground p-4 h-auto flex-col hover:bg-accent/90 sacred-glow transition-all duration-300"
            onClick={handleCrowdRouting}
            disabled={isRouting}
            data-testid="button-crowd-routing"
          >
            <i className={`fas fa-route text-2xl mb-2 ${isRouting ? 'animate-pulse' : ''}`}></i>
            <div className="text-sm font-bold">{isRouting ? 'Routing...' : 'Crowd Routing'}</div>
            <div className="text-xs opacity-80">Divine Path Guidance</div>
          </Button>

          <Button 
            className={`${isLockdown ? 'bg-red-600 hover:bg-red-700' : 'bg-secondary hover:bg-secondary/90'} text-secondary-foreground p-4 h-auto flex-col sacred-glow transition-all duration-300`}
            onClick={handleLockdown}
            data-testid="button-lockdown"
          >
            <i className={`fas ${isLockdown ? 'fa-lock text-red-200' : 'fa-shield-alt'} text-2xl mb-2`}></i>
            <div className="text-sm font-bold">{isLockdown ? 'Lift Lockdown' : 'Sacred Lockdown'}</div>
            <div className="text-xs opacity-80">{isLockdown ? 'Restore Access' : 'Divine Protection'}</div>
          </Button>
        </div>

        <div className="mt-4 p-3 bg-muted rounded-lg border border-border">
          <div className="flex items-center space-x-2 text-sm text-muted-foreground">
            <i className="fas fa-info-circle text-primary"></i>
            <span>Status: {isLockdown ? 'ðŸ”’ Emergency Protocol Active' : 'ðŸŸ¢ Normal Operations'}</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

